(1049, '宏观数据-部门人员分布图', 1, 'bar', 0, '', '', '{\r\n\"xaxis\":[{\"field\":\"deptName\",\"name\":\"部门\"}],\r\n\"yaxis\":[\r\n    {\"type\":\"value\",\"field\":\"count\",\"agg\":\"value\",\"name\":\"部门人数\",\"valOrAgg\":\"value\"}\r\n]}', '[]', '', 'SELECT IF(t3.id IS NOT NULL, t3.`name`, \'空\') AS deptName,count(1) as count, \r\nIF(t3.id IS NOT NULL, t3.`order`, 9999) AS deptOrder \r\nFROM zentao.`zt_user` AS t1 \r\nLEFT JOIN zentao.`zt_dept` AS t2 ON t1.dept = t2.id\r\nLEFT JOIN zentao.`zt_dept` AS t3 ON FIND_IN_SET(TRIM(\',\' FROM t3.path), TRIM(\',\' FROM t2.path)) AND t3.grade = \'1\'\r\nWHERE t1.deleted = \'0\'\r\nGROUP BY deptName, deptOrder \r\nORDER BY deptOrder  DESC', 1, '', 'admin', '2022-12-07 14:59:41', '', '2022-12-07 14:59:41', 0),
(1050, '宏观数据-公司角色分布图', 1, 'pie', 0, '', '', '{\"group\":[{\"field\":\"role\",\"name\":\"类型\"}],\"metric\":[{\"type\":\"agg\",\"field\":\"account\",\"agg\":\"count\",\"name\":\"人数\",\"valOrAgg\":\"count\"}]}', '[]', '', 'SELECT\n	account,\nCASE\n		ROLE \n		WHEN \'dev\' THEN\n		\'研发\' \n		WHEN \'qa\' THEN\r\n		\'测试\'\r\n		WHEN \'pm\' THEN\r\n		\'项目经理\'\r\n		WHEN \'others\' THEN\r\n		\'其他\'\r\n		WHEN \'td\' THEN\r\n		\'研发主管\'\r\n		WHEN \'pd\' THEN\r\n		\'产品主管\'\r\n		WHEN \'po\' THEN\r\n		\'产品经理\'\r\n		WHEN \'qd\' THEN\r\n		\'测试主管\'\r\n		WHEN \'top\' THEN\r\n		\'高层管理\'\r\n		ELSE\r\n		\'未知\'\r\n		END\r\n		role\n	FROM\n		zentao.`zt_user` \n	WHERE\n		deleted = \'0\' ', 1, '', 'admin', '2022-12-07 14:59:41', '', '2022-12-07 14:59:41', 0);
