(1051, '宏观数据-人员工龄分布图', 1, 'bar', 0, '', '', '{\r\n\"xaxis\":[{\"field\":\"joinDate\",\"name\":\"工龄\"}],\r\n\"yaxis\":[\r\n    {\"type\":\"value\",\"field\":\"count\",\"agg\":\"value\",\"name\":\"人数\",\"valOrAgg\":\"value\"}\r\n]}', '[]', '', 'SELECT count(1) as count, \'0-1年\' as joinDate FROM zentao.`zt_user` WHERE deleted = \'0\' AND `join` > DATE_SUB(NOW(), INTERVAL 1 YEAR)\r\nunion\r\nSELECT count(1) as count, \'1-3年\' as joinDate FROM zentao.`zt_user` WHERE deleted = \'0\' AND `join` > DATE_SUB(NOW(), INTERVAL 3 YEAR) AND `join` <= DATE_SUB(NOW(), INTERVAL 1 YEAR)\r\nunion\r\nSELECT count(1) as count, \'3-5年\' as joinDate FROM zentao.`zt_user` WHERE deleted = \'0\' AND `join` > DATE_SUB(NOW(), INTERVAL 5 YEAR) AND `join` <= DATE_SUB(NOW(), INTERVAL 3 YEAR)\r\nunion\r\nSELECT count(1) as count, \'5-10年\' as joinDate FROM zentao.`zt_user` WHERE deleted = \'0\' AND `join` > DATE_SUB(NOW(), INTERVAL 10 YEAR) AND `join` <= DATE_SUB(NOW(), INTERVAL 5 YEAR)\r\nunion\r\nSELECT count(1) as count, \'10年以上\' as joinDate FROM zentao.`zt_user` WHERE deleted = \'0\' AND `join` < DATE_SUB(NOW(), INTERVAL 10 YEAR) AND LEFT(`join`, 4) != \'0000\'\r\nunion                                                                                                                                                                                                                                                          \r\nSELECT count(1) as count, \'未知\' as joinDate FROM zentao.`zt_user` WHERE deleted = \'0\' AND LEFT(`join`, 4) = \'0000\'', 1, '', 'admin', '2022-12-07 14:59:41', '', '2022-12-07 14:59:41', 0),
(1055, '年度新增-一级项目集个数', 1, 'card', 0, '', '', '{\"value\": {\"type\": \"agg\", \"field\": \"id\", \"agg\": \"count\"}, \"title\": {\"type\": \"text\", \"name\": \"\"},\n\"type\": \"value\"\n}', '', NULL, 'SELECT\r\n	t1.`year`,\r\n	t2.id,\r\n	t2.name\r\nFROM\r\n	( SELECT DISTINCT YEAR ( `date` ) AS \'year\' FROM zt_action ) AS t1\r\n	LEFT JOIN (\r\n	SELECT\r\n		id, name,\r\n		YEAR ( openedDate ) AS `year` \r\n	FROM\r\n		zt_project \r\n	WHERE\r\n		`type` = \'program\' \r\n		AND deleted = \'0\' \r\n		AND grade = \'1\'  \r\n	) t2 ON t1.`year` = t2.`year`	\r\n WHERE t2.id IS NOT NULL', 1, '', '', '0000-00-00 00:00:00', '', '0000-00-00 00:00:00', 0),
(1056, '年度新增-产品个数', 1, 'card', 0, '', '', '{\"value\": {\"type\": \"agg\", \"field\": \"id\", \"agg\": \"count\"}, \"title\": {\"type\": \"text\", \"name\": \"\"},\n\"type\": \"value\"\n}', '', NULL, 'SELECT\r\n	t1.`year`,\r\n	t2.id,\r\n	t2.name\r\nFROM\r\n	( SELECT DISTINCT YEAR ( `date` ) AS \'year\' FROM zt_action ) AS t1\r\n	LEFT JOIN (\r\n	SELECT\r\n		id, name,\r\n		YEAR ( createdDate ) AS `year` \r\n	FROM\r\n		zt_product \r\n	WHERE\r\n		deleted = \'0\' \r\n		AND shadow = \'0\' \r\n	) t2 ON t1.`year` = t2.`year`	\r\n WHERE t2.id IS NOT NULL', 1, '', '', '0000-00-00 00:00:00', '', '0000-00-00 00:00:00', 0),
(1057, '年度新增-需求个数', 1, 'card', 0, '', '', '{\"value\": {\"type\": \"agg\", \"field\": \"id\", \"agg\": \"count\"}, \"title\": {\"type\": \"text\", \"name\": \"\"},\n\"type\": \"value\"\n}', '', NULL, 'SELECT\n	t1.`year`,\n	t2.id,\r\n	t2.title\nFROM\n	( SELECT DISTINCT YEAR ( `date` ) AS \'year\' FROM zt_action ) AS t1\n	LEFT JOIN ( SELECT id, title, YEAR ( openedDate ) AS `year` FROM zt_story WHERE deleted = \'0\' ) AS t2 ON t1.`year` = t2.`year`	\r\n WHERE t2.id IS NOT NULL', 1, '', '', '0000-00-00 00:00:00', '', '0000-00-00 00:00:00', 0),
(1058, '年度新增-Bug个数', 1, 'card', 0, '', '', '{\"value\": {\"type\": \"agg\", \"field\": \"id\", \"agg\": \"count\"}, \"title\": {\"type\": \"text\", \"name\": \"\"},\n\"type\": \"value\"\n}', '', NULL, 'SELECT\r\n	t1.`year`,\r\n	t2.id,\r\n	t2.title\r\nFROM\r\n	( SELECT DISTINCT YEAR ( `date` ) AS \'year\' FROM zt_action ) AS t1\r\n	LEFT JOIN ( SELECT id, title, YEAR ( openedDate ) AS `year` FROM zt_bug WHERE deleted = \'0\' ) AS t2 ON t1.`year` = t2.`year`	\r\n WHERE t2.id IS NOT NULL', 1, '', '', '0000-00-00 00:00:00', '', '0000-00-00 00:00:00', 0),
(1059, '年度新增-计划个数', 1, 'card', 0, '', '', '{\"value\": {\"type\": \"agg\", \"field\": \"id\", \"agg\": \"count\"}, \"title\": {\"type\": \"text\", \"name\": \"\"},\n\"type\": \"value\"\n}', '', NULL, 'SELECT\n	t1.`year`,\n	t2.id,\r\n	t2.title\nFROM\n	( SELECT DISTINCT YEAR ( `date` ) AS \'year\' FROM zt_action ) AS t1\n	LEFT JOIN ( SELECT id, title, YEAR ( createdDate ) AS `year` FROM zt_productplan WHERE deleted = \'0\') AS t2 ON t1.`year` = t2.`year`	\r\n WHERE t2.id IS NOT NULL', 1, '', '', '0000-00-00 00:00:00', '', '0000-00-00 00:00:00', 0),
(1060, '年度新增-项目个数', 1, 'card', 0, '', '', '{\"value\": {\"type\": \"agg\", \"field\": \"id\", \"agg\": \"count\"}, \"title\": {\"type\": \"text\", \"name\": \"\"},\n\"type\": \"value\"\n}', '', NULL, 'SELECT\r\n	t1.`year`,\r\n	t2.id,\r\n	t2.name\r\nFROM\r\n	( SELECT DISTINCT YEAR ( `date` ) AS \'year\' FROM zt_action ) AS t1\r\n	LEFT JOIN (\r\n	SELECT\r\n		id, name,\r\n		YEAR ( openedDate ) AS `year` \r\n	FROM\r\n		zt_project \r\n	WHERE\r\n		`type` = \'project\' \r\n		AND deleted = \'0\' \r\n	) t2 ON t1.`year` = t2.`year`	\r\n WHERE t2.id IS NOT NULL', 1, '', '', '0000-00-00 00:00:00', '', '0000-00-00 00:00:00', 0),
(1061, '年度新增-执行个数', 1, 'card', 0, '', '', '{\"value\": {\"type\": \"agg\", \"field\": \"id\", \"agg\": \"count\"}, \"title\": {\"type\": \"text\", \"name\": \"\"},\r\n\"type\": \"value\"\r\n}', '', NULL, 'SELECT t1.`year`, t2.id, t2.name\r\nFROM (SELECT DISTINCT YEAR(`date`) AS \'year\' FROM zt_action) AS t1\r\nLEFT JOIN (SELECT id,name, YEAR(openedDate) AS `year` FROM zt_project WHERE `type` IN ( \'sprint\', \'stage\', \'kanban\' ) AND deleted = \'0\' AND multiple = \'1\') AS t2 ON t1.`year` = t2.`year` \r\nWHERE t2.id IS NOT NULL', 1, '', '', '0000-00-00 00:00:00', '', '0000-00-00 00:00:00', 0),
