(1097, '年度排行-项目-人员投入榜', 1, 'bar', 0, '', '', '{\"xaxis\":[{\"field\":\"name\",\"name\":\"项目\"}],\"yaxis\":[{\"type\":\"value\",\"field\":\"number\",\"agg\":\"value\",\"name\":\"人数\",\"valOrAgg\":\"value\"}]}', '', '', 'SELECT tt.join as `year`, count(1) as number, tt.name from (\r\nselect \r\nt2.name, YEAR(t1.join) as `join`\r\nfrom zt_team t1 \r\nRIGHT JOIN zt_project t2 on t2.id = t1.root\r\nRIGHT JOIN zt_user t3 on t3.account = t1.account\r\nWHERE t1.type = \'project\'\r\nAND t2.deleted = \'0\'\r\n) tt\r\nGROUP BY tt.`name`, tt.join\r\nORDER BY tt.join, number desc, tt.name', 1, '', '', '0000-00-00 00:00:00', '', '0000-00-00 00:00:00', 0),
(1098, '年度排行-项目-工时消耗榜', 1, 'bar', 0, '', '', '{\"xaxis\":[{\"field\":\"project\",\"name\":\"项目\"}],\"yaxis\":[{\"type\":\"value\",\"field\":\"consumed\",\"agg\":\"value\",\"name\":\"消耗工时\",\"valOrAgg\":\"value\"}]}', '', NULL, 'SELECT \r\n  YEAR(t4.date) AS `year`,\r\n  t1.id,  \r\n  t1.name AS project, \r\n  ROUND(\r\n    SUM(t4.consumed), \r\n    2\r\n  ) AS consumed \r\nFROM \r\n  zt_project AS t1 \r\n  LEFT JOIN zt_project AS t2 ON t1.id = t2.parent \r\n  AND t2.deleted = \'0\' \r\n  AND t2.type IN (\'sprint\', \'stage\', \'kanban\') \r\n  LEFT JOIN zt_task AS t3 ON t2.id = t3.execution \r\n  AND t3.deleted = \'0\' \r\n  AND t3.status != \'cancel\' \r\n  LEFT JOIN zt_effort AS t4 ON t3.id = t4.objectID \r\n  AND t4.deleted = \'0\' \r\n  AND t4.objectType = \'task\' \r\nWHERE \r\n  t1.deleted = \'0\' \r\n  AND t1.type = \'project\' \r\n  AND t4.id IS NOT NULL \r\nGROUP BY \r\n  `year`, \r\n  id,\r\n  project \r\nORDER BY \r\n  `year`, \r\n  consumed DESC', 1, '', '', '0000-00-00 00:00:00', '', '0000-00-00 00:00:00', 0),
(1099, '年度排行-项目-完成需求条目榜', 1, 'bar', 0, '', '', '{\"xaxis\":[{\"field\":\"project\",\"name\":\"项目\"}],\"yaxis\":[{\"type\":\"value\",\"field\":\"story\",\"agg\":\"value\",\"name\":\"完成需求条目\",\"valOrAgg\":\"value\"}]}', '', NULL, 'SELECT \r\n  YEAR(t1.closedDate) AS `year`, \r\n  t1.id, \r\n  t1.project, \r\n  COUNT(1) AS story \r\nFROM \r\n  (\r\n    SELECT \r\n      DISTINCT t1.id, \r\n      t1.name AS project, \r\n      t4.id AS story, \r\n      t4.closedDate \r\n    FROM \r\n      zt_project AS t1 \r\n      LEFT JOIN zt_project AS t2 ON t1.id = t2.parent \r\n      AND t2.deleted = \'0\' \r\n      AND t2.type IN (\'sprint\', \'stage\', \'kanban\') \r\n      LEFT JOIN zt_projectstory AS t3 ON t2.id = t3.project \r\n      LEFT JOIN zt_story AS t4 ON t3.story = t4.id \r\n      AND t4.deleted = \'0\' \r\n      AND t4.closedReason = \'done\' \r\n    WHERE \r\n      t1.deleted = \'0\' \r\n      AND t1.type = \'project\' \r\n      AND t4.id IS NOT NULL\r\n  ) AS t1 \r\nGROUP BY \r\n  `year`, \r\n  id, \r\n  project \r\nORDER BY \r\n  `year`, \r\n  story DESC', 1, '', '', '0000-00-00 00:00:00', '', '0000-00-00 00:00:00', 0),
(1100, '年度排行-项目-完成需求规模榜', 1, 'bar', 0, '', '', '{\"xaxis\":[{\"field\":\"project\",\"name\":\"项目\"}],\"yaxis\":[{\"type\":\"value\",\"field\":\"story\",\"agg\":\"value\",\"name\":\"完成需求规模\",\"valOrAgg\":\"value\"}]}', '', NULL, 'SELECT \r\n  YEAR(t1.closedDate) AS `year`, \r\n  t1.id, \r\n  t1.project, \r\n  ROUND(\r\n    SUM(t1.estimate), \r\n    2\r\n  ) AS story \r\nFROM \r\n  (\r\n    SELECT \r\n      DISTINCT t1.id, \r\n      t1.name AS project, \r\n      t4.id AS story, \r\n      t4.estimate, \r\n      t4.closedDate \r\n    FROM \r\n      zt_project AS t1 \r\n      LEFT JOIN zt_project AS t2 ON t1.id = t2.parent \r\n      AND t2.deleted = \'0\' \r\n      AND t2.type IN (\'sprint\', \'stage\', \'kanban\') \r\n      LEFT JOIN zt_projectstory AS t3 ON t2.id = t3.project \r\n      LEFT JOIN zt_story AS t4 ON t3.story = t4.id \r\n      AND t4.deleted = \'0\' \r\n      AND t4.closedReason = \'done\' \r\n    WHERE \r\n      t1.deleted = \'0\' \r\n      AND t1.type = \'project\' \r\n      AND t4.id IS NOT NULL\r\n  ) AS t1 \r\nGROUP BY \r\n  `year`, \r\n  id, \r\n  project \r\nORDER BY \r\n  `year`, \r\n  story DESC', 1, '', '', '0000-00-00 00:00:00', '', '0000-00-00 00:00:00', 0),
(1101, '年度排行-产品-新增需求条目榜', 1, 'bar', 0, '', '', '{\"xaxis\":[{\"field\":\"product\",\"name\":\"产品\"}],\"yaxis\":[{\"type\":\"value\",\"field\":\"story\",\"agg\":\"value\",\"name\":\"新增需求条目\",\"valOrAgg\":\"value\"}]}', '', NULL, 'SELECT YEAR(t2.openedDate) AS `year`, t1.id,  t1.name AS product, COUNT(1) AS story\r\nFROM zt_product AS t1\r\nLEFT JOIN zt_story AS t2 ON t1.id = t2.product AND t2.deleted = \'0\'\r\nWHERE t1.deleted = \'0\' AND t1.shadow = \'0\' AND t1.vision = \'rnd\' AND t2.id IS NOT NULL\r\nGROUP BY `year`, id, product\r\nORDER BY `year`, story DESC', 1, '', '', '0000-00-00 00:00:00', '', '0000-00-00 00:00:00', 0),
(1102, '年度排行-产品-完成需求规模榜', 1, 'bar', 0, '', '', '{\"xaxis\":[{\"field\":\"product\",\"name\":\"产品\"}],\"yaxis\":[{\"type\":\"value\",\"field\":\"story\",\"agg\":\"value\",\"name\":\"完成需求规模\",\"valOrAgg\":\"value\"}]}', '', NULL, 'SELECT YEAR(t2.closedDate) AS `year`, t1.id, t1.name AS product, ROUND(SUM(t2.estimate), 1) AS story\r\nFROM zt_product AS t1\r\nLEFT JOIN zt_story AS t2 ON t1.id = t2.product AND t2.deleted = \'0\' AND t2.closedReason = \'done\'\r\nWHERE t1.deleted = \'0\' AND t1.shadow = \'0\' AND t1.vision = \'rnd\' AND t2.id IS NOT NULL\r\nGROUP BY `year`, id, product\r\nORDER BY `year`, story DESC', 1, '', '', '0000-00-00 00:00:00', '', '0000-00-00 00:00:00', 0),
(1103, '年度排行-产品-新增Bug条目榜', 1, 'bar', 0, '', '', '{\"xaxis\":[{\"field\":\"product\",\"name\":\"产品\"}],\"yaxis\":[{\"type\":\"value\",\"field\":\"bug\",\"agg\":\"value\",\"name\":\"新增Bug条目\",\"valOrAgg\":\"value\"}]}', '', NULL, 'SELECT YEAR(t2.openedDate) AS `year`, t1.id,  t1.name AS product, COUNT(1) AS bug\r\nFROM zt_product AS t1\r\nLEFT JOIN zt_bug AS t2 ON t1.id = t2.product AND t2.deleted = \'0\'\r\nWHERE t1.deleted = \'0\' AND t1.shadow = \'0\' AND t1.vision = \'rnd\' AND t2.id IS NOT NULL\r\nGROUP BY `year`, id, product\r\nORDER BY `year`, bug DESC', 1, '', '', '0000-00-00 00:00:00', '', '0000-00-00 00:00:00', 0),
(1104, '年度排行-产品-修复Bug条目榜', 1, 'bar', 0, '', '', '{\"xaxis\":[{\"field\":\"product\",\"name\":\"产品\"}],\"yaxis\":[{\"type\":\"value\",\"field\":\"bug\",\"agg\":\"value\",\"name\":\"修复Bug条目\",\"valOrAgg\":\"value\"}]}', '', NULL, 'SELECT YEAR(t2.closedDate) AS `year`, t1.id, t1.name AS product, COUNT(1) AS bug\r\nFROM zt_product AS t1\r\nLEFT JOIN zt_bug AS t2 ON t1.id = t2.product AND t2.deleted = \'0\' AND t2.resolution = \'fixed\' AND t2.status = \'closed\'\r\nWHERE t1.deleted = \'0\' AND t1.shadow = \'0\' AND t1.vision = \'rnd\' AND t2.id IS NOT NULL\r\nGROUP BY `year`, id, product\r\nORDER BY `year`, bug DESC', 1, '', '', '0000-00-00 00:00:00', '', '0000-00-00 00:00:00', 0),
(1105, '年度排行-个人-创建需求条目榜', 1, 'bar', 0, '', '', '{\"xaxis\":[{\"field\":\"realname\",\"name\":\"用户\"}],\"yaxis\":[{\"type\":\"value\",\"field\":\"count\",\"agg\":\"value\",\"name\":\"创建需求条目\",\"valOrAgg\":\"value\"}]}', '', NULL, 'SELECT \r\nYEAR(t3.openedDate) AS `year`,t2.realname,count(1) AS count\r\nFROM zt_action AS t1 RIGHT JOIN zt_user AS t2 ON t1.actor=t2.account LEFT JOIN zt_story AS t3 ON t1.objectID=t3.id\r\nWHERE t1.objectType=\'story\' AND t1.action=\'opened\' AND t3.deleted=\'0\'\r\nGROUP BY `year`,t2.account ORDER BY `year`,count DESC', 1, '', '', '0000-00-00 00:00:00', '', '0000-00-00 00:00:00', 0),
(1106, '年度排行-个人-创建用例条目榜', 1, 'bar', 0, '', '', '{\"xaxis\":[{\"field\":\"realname\",\"name\":\"用户\"}],\"yaxis\":[{\"type\":\"value\",\"field\":\"count\",\"agg\":\"value\",\"name\":\"创建需用例条目\",\"valOrAgg\":\"value\"}]}', '', '', 'SELECT \nYEAR(t3.openedDate) AS `year`,t2.realname,count(1) AS count\nFROM zt_action AS t1 RIGHT JOIN zt_user AS t2 ON t1.actor=t2.account LEFT JOIN zt_case AS t3 ON t1.objectID=t3.id\nWHERE t1.objectType=\'case\' AND t1.action=\'opened\' AND t3.deleted=\'0\'\nGROUP BY `year`,t2.account ORDER BY `year`,count DESC', 1, '', '', '0000-00-00 00:00:00', '', '0000-00-00 00:00:00', 0),
(1107, '年度排行-个人-创建Bug条目榜', 1, 'bar', 0, '', '', '{\"xaxis\":[{\"field\":\"realname\",\"name\":\"用户\"}],\"yaxis\":[{\"type\":\"value\",\"field\":\"count\",\"agg\":\"value\",\"name\":\"创建Bug条目\",\"valOrAgg\":\"value\"}]}', '', NULL, 'SELECT \nYEAR(t3.openedDate) AS `year`,t2.realname,count(1) AS count\nFROM zt_action AS t1 RIGHT JOIN zt_user AS t2 ON t1.actor=t2.account LEFT JOIN zt_bug AS t3 ON t1.objectID=t3.id\nWHERE t1.objectType=\'bug\' AND t1.action=\'opened\' AND t3.deleted=\'0\'\nGROUP BY `year`,t2.account ORDER BY `year`,count DESC', 1, '', '', '0000-00-00 00:00:00', '', '0000-00-00 00:00:00', 0),
(1108, '年度排行-个人-修复Bug条目榜', 1, 'bar', 0, '', '', '{\"xaxis\":[{\"field\":\"realname\",\"name\":\"用户\"}],\"yaxis\":[{\"type\":\"value\",\"field\":\"count\",\"agg\":\"value\",\"name\":\"修复Bug条目\",\"valOrAgg\":\"value\"}]}', '', NULL, 'SELECT \r\nYEAR(t3.openedDate) AS `year`,t2.realname,count(DISTINCT t3.id) AS count\r\nFROM zt_action AS t1 RIGHT JOIN zt_user AS t2 ON t1.actor=t2.account LEFT JOIN zt_bug AS t3 ON t1.objectID=t3.id\r\nWHERE t1.objectType=\'bug\' AND t1.action=\'resolved\' AND t3.deleted=\'0\'\r\nGROUP BY `year`,t2.account ORDER BY `year`,count DESC', 1, '', '', '0000-00-00 00:00:00', '', '0000-00-00 00:00:00', 0),
(1109, '年度排行-个人-工时消耗榜', 1, 'bar', 0, '', '', '{\"xaxis\":[{\"field\":\"realname\",\"name\":\"用户\"}],\"yaxis\":[{\"type\":\"value\",\"field\":\"consumed\",\"agg\":\"value\",\"name\":\"消耗工时\",\"valOrAgg\":\"value\"}]}', '', NULL, 'SELECT YEAR(t1.date) AS `year`, t2.realname, ROUND(SUM(t1.consumed),1) AS consumed\nFROM zt_effort AS t1 LEFT JOIN zt_user AS t2 ON t1.account = t2.account\nWHERE t1.deleted = \'0\' AND t2.deleted = \'0\'\nGROUP BY `year`, realname\nORDER BY `year`, consumed DESC', 1, '', '', '0000-00-00 00:00:00', '', '0000-00-00 00:00:00', 0),
(1110, '年度排行-个人-禅道操作次数榜', 1, 'bar', 0, '', '', '{\"xaxis\":[{\"field\":\"realname\",\"name\":\"用户\"}],\"yaxis\":[{\"type\":\"value\",\"field\":\"count\",\"agg\":\"value\",\"name\":\"操作次数\",\"valOrAgg\":\"value\"}]}', '', NULL, 'SELECT YEAR(t1.date) AS `year`,IFNULL(t2.realname,t1.actor) AS realname,count(1) AS count\r\nFROM zt_action t1 LEFT JOIN zt_user AS t2 ON t1.actor=t2.account\r\nGROUP BY `year`,t1.actor\r\nORDER BY `year`, `count` DESC', 1, '', '', '0000-00-00 00:00:00', '', '0000-00-00 00:00:00', 0);
